@import "tailwindcss";
@import "tw-animate-css";

/* Force Tailwind to scan all source files */
@source "./**/*.{html,ts,tsx,js,jsx}";

/* Safelist critical responsive utilities for sidebar - prevents purging in production */
@layer utilities {
  .sidebar-safelist {
    /* Force inclusion of these classes by referencing them */
    @apply hidden md:flex w-64 fixed left-0 top-0 h-screen;
    @apply md:ml-64 md:pb-0 md:hidden;
  }
}

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 2px);
  --radius-md: calc(var(--radius));
  --radius-lg: calc(var(--radius) + 2px);
  --radius-xl: calc(var(--radius) + 4px);

  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));

  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));

  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));

  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));

  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));

  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));

  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));

  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));

  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));

  --color-chart-1: hsl(var(--chart-1));
  --color-chart-2: hsl(var(--chart-2));
  --color-chart-3: hsl(var(--chart-3));
  --color-chart-4: hsl(var(--chart-4));
  --color-chart-5: hsl(var(--chart-5));

  --color-sidebar: hsl(var(--sidebar));
  --color-sidebar-foreground: hsl(var(--sidebar-foreground));
  --color-sidebar-primary: hsl(var(--sidebar-primary));
  --color-sidebar-primary-foreground: hsl(var(--sidebar-primary-foreground));
  --color-sidebar-accent: hsl(var(--sidebar-accent));
  --color-sidebar-accent-foreground: hsl(var(--sidebar-accent-foreground));
  --color-sidebar-border: hsl(var(--sidebar-border));
  --color-sidebar-ring: hsl(var(--sidebar-ring));

  --font-sans: 'Inter', 'Roboto', sans-serif;
  --font-display: 'Oswald', 'Montserrat', sans-serif;
  --font-mono: 'JetBrains Mono', 'Roboto Mono', monospace;
}

/* MISSION CONTROL THEME - Cohete Brands OS */
:root {
  /* Dark Mode by default as per Mission Control spec */
  --background: 0 0% 2%; /* #050505 Deep Space */
  --foreground: 0 0% 100%; /* #FFFFFF Starlight White */

  --card: 0 0% 10%; /* #1A1A1A Orbital Grey */
  --card-foreground: 0 0% 100%;

  --popover: 0 0% 10%;
  --popover-foreground: 0 0% 100%;

  --primary: 43 100% 50%; /* #FFB800 Ignition Orange */
  --primary-foreground: 0 0% 0%;

  --secondary: 0 0% 20%; /* #333333 Moon Dustish */
  --secondary-foreground: 0 0% 100%;

  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 60%;

  --accent: 43 100% 50%;
  --accent-foreground: 0 0% 0%;

  --destructive: 0 83% 66%; /* #FF5252 Error Red */
  --destructive-foreground: 0 0% 100%;

  --border: 0 0% 20%; /* #333333 Moon Dust */
  --input: 0 0% 20%;
  --ring: 43 100% 50%;

  --radius: 0.125rem; /* 2px - Sharp/Industrial */

  /* Chart colors adapted for dark mode */
  --chart-1: 43 100% 50%; /* Orange */
  --chart-2: 151 100% 45%; /* Green #00E676 */
  --chart-3: 50 100% 50%; /* Yellow #FFD600 */
  --chart-4: 217 91% 60%; /* Blue counterpart */
  --chart-5: 280 100% 60%; /* Purple counterpart */

  --sidebar: 0 0% 5%; /* Slightly lighter than background */
  --sidebar-foreground: 0 0% 100%;
  --sidebar-primary: 43 100% 50%;
  --sidebar-primary-foreground: 0 0% 0%;
  --sidebar-accent: 0 0% 15%;
  --sidebar-accent-foreground: 0 0% 100%;
  --sidebar-border: 0 0% 20%;
  --sidebar-ring: 43 100% 50%;
}

.dark {
  /* Same as root since we are dark-first */
  --background: 0 0% 2%;
  --foreground: 0 0% 100%;
  --card: 0 0% 10%;
  --card-foreground: 0 0% 100%;
  --popover: 0 0% 10%;
  --popover-foreground: 0 0% 100%;
  --primary: 43 100% 50%;
  --primary-foreground: 0 0% 0%;
  --secondary: 0 0% 20%;
  --secondary-foreground: 0 0% 100%;
  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 60%;
  --accent: 43 100% 50%;
  --accent-foreground: 0 0% 0%;
  --destructive: 0 83% 66%;
  --destructive-foreground: 0 0% 100%;
  --border: 0 0% 20%;
  --input: 0 0% 20%;
  --ring: 43 100% 50%;
  --sidebar: 0 0% 5%;
  --sidebar-foreground: 0 0% 100%;
  --sidebar-primary: 43 100% 50%;
  --sidebar-primary-foreground: 0 0% 0%;
  --sidebar-accent: 0 0% 15%;
  --sidebar-accent-foreground: 0 0% 100%;
  --sidebar-border: 0 0% 20%;
  --sidebar-ring: 43 100% 50%;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
    background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 200 200' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.65' numOctaves='3' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.05'/%3E%3C/svg%3E");
    background-attachment: fixed;
  }
  h1, h2, h3, h4, h5, h6 {
    @apply font-display uppercase tracking-wide;
  }
}

@layer utilities {
  .text-glow {
    text-shadow: 0 0 10px hsl(var(--primary) / 0.5);
  }

  .border-glow {
    box-shadow: 0 0 10px hsl(var(--primary) / 0.3);
  }

  /* Custom scrollbar for the industrial feel */
  ::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }
  ::-webkit-scrollbar-track {
    background: hsl(var(--background));
  }
  ::-webkit-scrollbar-thumb {
    background: hsl(var(--border));
    border-radius: 0;
  }
  ::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--primary));
  }
}

