import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { 
  insertCampaignSchema, 
  updateCampaignSchema,
  insertClientAccountSchema,
  insertTeamSchema,
  updateTeamSchema,
  insertTeamAssignmentSchema,
  insertResourceSchema
} from "@shared/schema";
import { z } from "zod";
import { setupWebSocket, broadcastCampaignUpdate } from "./websocket";
import OpenAI from "openai";
import {
  agentTools,
  getCampaigns,
  getAnalytics,
  getTeam,
  getClientStatus,
  getResources,
  getDatabaseStats,
  proposeCreateCampaign,
  proposeUpdateCampaign,
  proposeDeleteCampaign,
  executeApprovedAction,
  type AgentToolContext,
} from "./agent-tools";

const openai = new OpenAI({
  baseURL: process.env.AI_INTEGRATIONS_OPENAI_BASE_URL,
  apiKey: process.env.AI_INTEGRATIONS_OPENAI_API_KEY,
});

export async function registerRoutes(app: Express): Promise<Server> {

  app.get("/api/campaigns", async (req, res) => {
    try {
      const campaigns = await storage.getCampaigns();
      res.json(campaigns);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch campaigns" });
    }
  });

  app.get("/api/campaigns/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const campaign = await storage.getCampaignById(id);
      if (!campaign) {
        return res.status(404).json({ error: "Campaign not found" });
      }
      res.json(campaign);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch campaign" });
    }
  });

  app.post("/api/campaigns", async (req, res) => {
    try {
      const validatedData = insertCampaignSchema.parse(req.body);
      const campaign = await storage.createCampaign(validatedData);
      await broadcastCampaignUpdate(campaign);
      res.status(201).json(campaign);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create campaign" });
    }
  });

  app.patch("/api/campaigns/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = updateCampaignSchema.parse(req.body);
      const campaign = await storage.updateCampaign(id, validatedData);
      if (!campaign) {
        return res.status(404).json({ error: "Campaign not found" });
      }
      await broadcastCampaignUpdate(campaign);
      res.json(campaign);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update campaign" });
    }
  });

  app.delete("/api/campaigns/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteCampaign(id);
      if (!deleted) {
        return res.status(404).json({ error: "Campaign not found" });
      }
      await broadcastCampaignUpdate();
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete campaign" });
    }
  });

  app.get("/api/metrics", async (req, res) => {
    try {
      const metrics = await storage.getSystemMetrics();
      res.json(metrics);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch metrics" });
    }
  });

  app.get("/api/telemetry", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 24;
      const data = await storage.getTelemetryData(limit);
      res.json(data);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch telemetry data" });
    }
  });

  app.get("/api/clients", async (req, res) => {
    try {
      res.setHeader('Cache-Control', 'no-store, no-cache, must-revalidate, proxy-revalidate');
      res.setHeader('Pragma', 'no-cache');
      res.setHeader('Expires', '0');
      const accounts = await storage.getClientAccounts();
      res.json(accounts);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch client accounts" });
    }
  });

  app.post("/api/clients", async (req, res) => {
    try {
      const validatedData = insertClientAccountSchema.parse(req.body);
      const account = await storage.createClientAccount(validatedData);
      res.status(201).json(account);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create client account" });
    }
  });

  app.patch("/api/clients/:campaignId", async (req, res) => {
    try {
      const campaignId = parseInt(req.params.campaignId);
      const validatedData = insertClientAccountSchema.partial().parse(req.body);
      const account = await storage.updateClientAccount(campaignId, validatedData);
      if (!account) {
        return res.status(404).json({ error: "Client account not found" });
      }
      res.json(account);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update client account" });
    }
  });

  app.delete("/api/clients/:campaignId", async (req, res) => {
    try {
      const campaignId = parseInt(req.params.campaignId);
      const deleted = await storage.deleteClientAccount(campaignId);
      if (!deleted) {
        return res.status(404).json({ error: "Client account not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete client account" });
    }
  });

  app.get("/api/team", async (req, res) => {
    try {
      const allTeam = await storage.getTeam();
      res.json(allTeam);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch team" });
    }
  });

  app.post("/api/team", async (req, res) => {
    try {
      const validatedData = insertTeamSchema.parse(req.body);
      const person = await storage.createTeam(validatedData);
      res.status(201).json(person);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create team member" });
    }
  });

  app.patch("/api/team/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = updateTeamSchema.parse(req.body);
      const person = await storage.updateTeam(id, validatedData);
      if (!person) {
        return res.status(404).json({ error: "Team member not found" });
      }
      res.json(person);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update team member" });
    }
  });

  app.delete("/api/team/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteTeam(id);
      if (!deleted) {
        return res.status(404).json({ error: "Team member not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete team member" });
    }
  });

  app.get("/api/team/assignments", async (req, res) => {
    try {
      const assignments = await storage.getTeamAssignments();
      res.json(assignments);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch assignments" });
    }
  });

  app.post("/api/team/assignments", async (req, res) => {
    try {
      const validatedData = insertTeamAssignmentSchema.parse(req.body);
      const assignment = await storage.createTeamAssignment(validatedData);
      res.status(201).json(assignment);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create assignment" });
    }
  });

  app.delete("/api/team/assignments/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteTeamAssignment(id);
      if (!deleted) {
        return res.status(404).json({ error: "Assignment not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete assignment" });
    }
  });

  app.get("/api/resources", async (req, res) => {
    try {
      const resources = await storage.getResources();
      res.json(resources);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch resources" });
    }
  });

  app.post("/api/resources", async (req, res) => {
    try {
      const validatedData = insertResourceSchema.parse(req.body);
      const resource = await storage.createResource(validatedData);
      res.status(201).json(resource);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to create resource entry" });
    }
  });

  app.patch("/api/resources/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const validatedData = insertResourceSchema.partial().parse(req.body);
      const resource = await storage.updateResource(id, validatedData);
      if (!resource) {
        return res.status(404).json({ error: "Resource entry not found" });
      }
      res.json(resource);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ error: error.errors });
      }
      res.status(500).json({ error: "Failed to update resource entry" });
    }
  });

  app.delete("/api/resources/:id", async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const deleted = await storage.deleteResource(id);
      if (!deleted) {
        return res.status(404).json({ error: "Resource entry not found" });
      }
      res.status(204).send();
    } catch (error) {
      res.status(500).json({ error: "Failed to delete resource entry" });
    }
  });

  app.get("/api/analytics", async (req, res) => {
    try {
      const campaigns = await storage.getCampaigns();
      const telemetry = await storage.getTelemetryData(100);

      const analytics = {
        totalCampaigns: campaigns.length,
        activeCampaigns: campaigns.filter(c => c.status === "Active" || c.status === "In Progress").length,
        completedCampaigns: campaigns.filter(c => c.status === "Completed").length,
        averageProgress: campaigns.length > 0 
          ? Math.round(campaigns.reduce((sum, c) => sum + c.progress, 0) / campaigns.length)
          : 0,
        priorityBreakdown: {
          critical: campaigns.filter(c => c.priority === "Critical").length,
          high: campaigns.filter(c => c.priority === "High").length,
          medium: campaigns.filter(c => c.priority === "Medium").length,
          low: campaigns.filter(c => c.priority === "Low").length,
        },
        recentActivity: telemetry.slice(0, 20).reverse(),
      };

      res.json(analytics);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch analytics" });
    }
  });

  // AI Agent Chat Endpoint
  app.post("/api/agent/chat", async (req, res) => {
    try {
      const { messages, executeAction } = req.body;

      if (!messages || !Array.isArray(messages)) {
        return res.status(400).json({ error: "Messages array is required" });
      }

      const ctx: AgentToolContext = { storage };

      // If executing an approved action
      if (executeAction) {
        const { actionType, actionData } = executeAction;
        const result = await executeApprovedAction(ctx, actionType, actionData);
        await broadcastCampaignUpdate({ id: 0, campaignCode: "SYSTEM", name: "Agent Action", clientName: "System", channel: "Internal", status: "Active", progress: 0, priority: "High", budget: 0, spend: 0, targetAudience: null });
        return res.json({
          role: "assistant",
          content: result.message,
          executedAction: true,
        });
      }

      // System prompt for the agent
      const systemMessage = {
        role: "system" as const,
        content: `You are the Marketing Operations AI Assistant for Cohete Brands marketing agency. You help users query and manage their marketing campaigns and client operations.

Current date and time: ${new Date().toISOString()}

You have access to the following capabilities:

QUERY FUNCTIONS (read-only, no approval needed):
- get_campaigns: View all marketing campaigns with status, progress, budget, and client info
- get_analytics: View campaign analytics including performance metrics and ROI
- get_team: View all team members with roles and assignments
- get_client_status: View client accounts with health scores and budgets
- get_resources: View marketing resources and deliverables status
- get_database_stats: View comprehensive database statistics

ACTION FUNCTIONS (require user approval):
- propose_create_campaign: Suggest creating a new marketing campaign (user must approve)
- propose_update_campaign: Suggest updating an existing campaign (user must approve)
- propose_delete_campaign: Suggest deleting a campaign (user must approve)

IMPORTANT GUIDELINES:
1. Always be helpful, concise, and professional
2. Use query functions to answer questions about campaigns, clients, and team
3. For any action (create/update/delete), use the "propose" functions which require user approval
4. When proposing actions, clearly explain what will happen
5. Provide marketing metrics in a clear, formatted way
6. If asked about live/current data, use the query functions to get real-time information
7. Be proactive in suggesting campaign optimizations and insights

Remember: Actions require explicit user approval before execution.`,
      };

      // Call GPT-5 with function calling
      const completion = await openai.chat.completions.create({
        model: "gpt-5",
        messages: [systemMessage, ...messages],
        tools: agentTools,
        tool_choice: "auto",
        max_completion_tokens: 2048,
      });

      const responseMessage = completion.choices[0]?.message;

      if (!responseMessage) {
        throw new Error("No response from AI");
      }

      // If the model wants to call functions
      if (responseMessage.tool_calls && responseMessage.tool_calls.length > 0) {
        const toolResults = [];

        for (const toolCall of responseMessage.tool_calls) {
          if (toolCall.type !== 'function') continue;

          const functionName = toolCall.function.name;
          const functionArgs = JSON.parse(toolCall.function.arguments);

          let result;

          // Execute the appropriate function
          switch (functionName) {
            case "get_campaigns":
              result = await getCampaigns(ctx);
              break;
            case "get_analytics":
              result = await getAnalytics(ctx);
              break;
            case "get_team":
              result = await getTeam(ctx);
              break;
            case "get_client_status":
              result = await getClientStatus(ctx);
              break;
            case "get_resources":
              result = await getResources(ctx);
              break;
            case "get_database_stats":
              result = await getDatabaseStats(ctx);
              break;
            case "propose_create_campaign":
              result = proposeCreateCampaign(functionArgs);
              break;
            case "propose_update_campaign":
              result = proposeUpdateCampaign(functionArgs.campaignId, functionArgs.updates);
              break;
            case "propose_delete_campaign":
              result = proposeDeleteCampaign(functionArgs.campaignId);
              break;
            default:
              result = { error: `Unknown function: ${functionName}` };
          }

          toolResults.push({
            tool_call_id: toolCall.id,
            role: "tool" as const,
            name: functionName,
            content: JSON.stringify(result),
          });
        }

        // Get the final response from the model with tool results
        const finalCompletion = await openai.chat.completions.create({
          model: "gpt-5",
          messages: [
            systemMessage,
            ...messages,
            responseMessage,
            ...toolResults,
          ],
          max_completion_tokens: 2048,
        });

        const finalMessage = finalCompletion.choices[0]?.message;

        // Check if any tool results contain approval proposals
        const proposedActions = toolResults
          .map((tr) => JSON.parse(tr.content))
          .filter((content) => content.requiresApproval);

        return res.json({
          role: "assistant",
          content: finalMessage?.content || "I've processed your request.",
          toolCalls: responseMessage.tool_calls,
          toolResults,
          proposedActions: proposedActions.length > 0 ? proposedActions : undefined,
        });
      }

      // No function calls, just return the response
      return res.json({
        role: "assistant",
        content: responseMessage.content || "I'm here to help with your marketing operations dashboard.",
      });

    } catch (error: any) {
      console.error("Agent chat error:", error);
      res.status(500).json({ 
        error: "Failed to process chat message",
        details: error.message 
      });
    }
  });

  const httpServer = createServer(app);

  setupWebSocket(httpServer);

  return httpServer;
}

